            StringWriter result3 = new StringWriter();
            Velocity.invokeVelocimacro("floog", "test", new String[2], 
                                        context, result3);
*/
            if (!result1.equals(compare1.toString()))
            {
                fail("Output incorrect.");
            }
        }
        catch (Exception e)
        {
            fail(e.getMessage());
        }
    }
